	public static JLabel[] Chart(double[] TimePos, int dir) { //TimePos = Delay Time
		JLabel[] enemyLane = new JLabel[TimePos.length];
		int PosY = 0;
		if(dir == 1) {PosY = 177;} else PosY = 680-177-75;
		
		for(int n = 0; n < TimePos.length; n++) {
			JLabel enemies = new JLabel(Enemy);
			
			if(n == 0) {
				enemies.setBounds((int) TimePos[0], PosY, 75, 75); 
				HorizontalPos += (double) TimePos[0];
				//System.out.println("[Chart Building] 1st Enemy pix distance: "+TimePos[0]);
			}
			else {
				HorizontalPos += TimePos[n]*Pix_per_Beat;
				enemies.setBounds((int) Math.round(HorizontalPos), PosY, 75, 75);
				//System.out.println("[Chart Building] Enemy #"+(n+1)+" pix distance: "+HorizontalPos);
			}
			enemyLane[n] = enemies;
		}; HorizontalPos = 0; 
		return enemyLane;
	}
	
	public static JLabel[] getUpperChart(int num) {
		ChartNum(num);
		UpperChart = Chart(upper, 1);
		return UpperChart;
	}
	
	public static JLabel[] getLowerChart(int num) {
		ChartNum(num);
		LowerChart = Chart(lower, 2);
		return LowerChart;
	}
